import React, { Component } from 'react'
import Img1 from '../images/blastoids.jpeg'
import Img2 from '../images/charzard.jpeg'
import Img3 from '../images/mewtwo.jpeg'
import Img4 from '../images/machop.jpeg'
import Img5 from '../images/mimikyu.jpeg'
import Img6 from '../images/pikachu.jpeg' 
import Img7 from '../images/raticate.jpeg' 
import Img8 from '../images/sandshrew.jpeg'
import Img9 from '../images/snorlax.jpeg'
import Img10 from '../images/squirtle.jpeg' 

const images = [
    { id: 1, src:  Img1},
    { id: 2, src:  Img2},
    { id: 3, src:  Img3},
    { id: 4, src:  Img4},
    { id: 5, src:  Img5},
    { id: 6, src: Img6},
    { id: 7, src: Img7},
    { id: 8, src:  Img8},
    { id: 9, src:  Img9},
    { id: 10, src: Img10},
]

let shuffle = (a) => {
    var i, j, temp
    for (i = a.length - 1; i > 0; i -= 1) {
        j = Math.floor(Math.random() * (i + 1))
        temp = a[i]
        a[i] = a[j]
        a[j] = temp
    }
}

shuffle(images)
//wrap image tag (within the .map function with the div tag that has the col classname/or Col if using react-bootstrap)
//outer container div would be the Row
class Card extends Component {

    render() {
        return (
            <div>

                {
                    images.map(({ id, src }) =>
                        <img key={id} src={src} alt='' />
                    )
                }

            </div>
        )
    }
}

export default Card
