            { id: 4, src: Img4 },
            { id: 5, src: Img5 },
            { id: 6, src: Img6 },
            { id: 7, src: Img7 },
            { id: 8, src: Img8 },
            { id: 9, src: Img9 },
            { id: 10, src: Img10 },
        ]
    }

    handleClick = id => {
        const shuffle = (a) => {
            let i, j, temp
            for (i = a.length - 1; i > 0; i -= 1) {
                j = Math.floor(Math.random() * (i + 1))
                temp = a[i]
                // a[i] = a[j]
                a[j] = temp
                console.log(temp)
            }
            this.setState({
                images:  [...this.state.images, temp]
            })
        }
        shuffle(this.state.images)
        console.log(id)
    } 
    render() {
        return (
            <div>

                {
                   this.state.images.map(({ id, src }) =>
                        <img style={cardStyle} key={id} src={src} alt='' onClick={this.handleClick.bind(this, id)} />
                    )
                }

            </div>
        )
    }
}

export default Card
