import React, { Component } from 'react'

const images = [
    { id: 1, src: '../images/blastoids.jpeg' },
    { id: 2, src: '../images/charzard.jpeg' },
    { id: 3, src: '../images/mewtwo.jpeg' },
    { id: 4, src: '../images/machop.jpeg' },
    { id: 5, src: '../images/mimikyu.jpeg' },
    { id: 6, src: '../images/pikachu.jpeg' },
    { id: 7, src: '../images/raticate.jpeg' },
    { id: 8, src: '../images/sandshrew.jpeg' },
    { id: 9, src: '../images/snorlax.jpeg' },
    { id: 10, src: '../images/squirtle.jpeg' },
]

let shuffle = (a) => {
    var i, j, temp
    for (i = a.length - 1; i > 0; i -= 1) {
        j = Math.floor(Math.random() * (i + 1))
        temp = a[i]
        a[i] = a[j]
        a[j] = temp
    }
}

shuffle(images)

class Card extends Component {

    render() {
        return (
            <>

                {
                    images.map(({ id, src }) =>
                        <img key={id} src={src} alt='' />
                    )
                }

            </>
        )
    }
}

export default Card
